package java_learn_code.no_09_java_basic_syntax;
/**
 * if语句例子
 * @author Jole
 *
 */
public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		if(i==5) {
			System.out.println("if中的条件是true");
			i++;
		}
		System.out.println("i="+i);
	}

}
